package Basics;
// class name Student has been declared
public class Student {
	int id=2 ; String name= "Ram"; String address= "Mumbai"; //variable declaration
	
	

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Student st = new Student(); //creating object of student
		System.out.println("Student id is " + st.id);
		System.out.println("Student name is " + st.name);
		System.out.println("Student address is " + st.address);

	}

}
